// QR Code generation library (simplified version)
var QRCode = function(element, options) {
    this.element = element;
    this.options = options || {};
    this.generate();
};

QRCode.prototype.generate = function() {
    // Simple QR code generation using Google Charts API
    var size = this.options.width || 256;
    var data = encodeURIComponent(this.options.text || '');
    var img = document.createElement('img');
    img.src = `https://api.qrserver.com/v1/create-qr-code/?size=${size}x${size}&data=${data}`;
    img.alt = 'QR Code';
    img.style.maxWidth = '100%';
    
    // Clear existing content and add QR code
    this.element.innerHTML = '';
    this.element.appendChild(img);
};

// Export for use
window.QRCode = QRCode;